server/ai-gesture-service.ts.content:
import OpenAI from 'openai';

// the newest OpenAI model is "gpt-4o", not "gpt-4". do not change this unless explicitly requested by the user
const openai = new OpenAI({
  apiKey: process.env.OPENAI_API_KEY || '',
});

export interface AIGestureResult {
  gestureDetected: boolean;
  gestureType: string;
  confidence: number;
  description: string;
  isISLGesture: boolean;
}

export async function analyzeGestureImage(base64Image: string): Promise<AIGestureResult> {
  try {
    const response = await openai.chat.completions.create({
      model: "gpt-4o",
      messages: [
        {
          role: "system",
          content: `You are an expert in Indian Sign Language (ISL) gesture recognition. Analyze the image and identify if there's a clear hand gesture being performed. 

If you see a hand gesture, classify it as one of these types:
- "greeting" (hello, namaste, waving, open palm)
- "question" (pointing, questioning gestures)
- "status" (thumbs up/down, okay signs)
- "gratitude" (thank you gestures, prayer hands, folded hands)
- "emergency" (help signals, urgent gestures, closed fist)
- "number" (counting gestures, finger counting)
- "direction" (pointing directions)

Respond in JSON format with:
{
  "gestureDetected": boolean,
  "gestureType": string (one of the types above),
  "confidence": number (0.0-1.0),
  "description": string (brief description of what you see),
  "isISLGesture": boolean (true if this looks like a deliberate sign language gesture)
}

Only respond with high confidence (>0.7) if you clearly see a deliberate hand gesture.`
        },
        {
          role: "user",
          content: [
            {
              type: "text",
              text: "Analyze this image for Indian Sign Language hand gestures. Look for clear, deliberate hand positions and movements."
            },
            {
              type: "image_url",
              image_url: {
                url: `data:image/jpeg;base64,${base64Image}`
              }
            }
          ]
        }
      ],
      max_tokens: 300,
      response_format: { type: "json_object" }
    });

    const result = JSON.parse(response.choices[0].message.content || '{}');

    return {
      gestureDetected: result.gestureDetected || false,
      gestureType: result.gestureType || '',
      confidence: result.confidence || 0,
      description: result.description || '',
      isISLGesture: result.isISLGesture || false
    };

  } catch (error) {
    console.error('OpenAI gesture analysis failed:', error);
    return {
      gestureDetected: false,
      gestureType: '',
      confidence: 0,
      description: 'Analysis failed',
      isISLGesture: false
    };
  }
}

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "‚Ä¶";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { insertGestureSchema } from "@shared/schema";
import { analyzeGestureImage } from "./ai-gesture-service";
import { z } from "zod";

export async function registerRoutes(app: Express): Promise<Server> {

  // Create a new gesture record
  app.post("/api/gestures", async (req, res) => {
    try {
      const gestureData = insertGestureSchema.parse(req.body);
      const gesture = await storage.createGesture(gestureData);
      res.json(gesture);
    } catch (error) {
      if (error instanceof z.ZodError) {
        res.status(400).json({ error: "Invalid gesture data", details: error.errors });
      } else {
        res.status(500).json({ error: "Failed to create gesture" });
      }
    }
  });

  // Get gestures by region
  app.get("/api/gestures/region/:region", async (req, res) => {
    try {
      const region = req.params.region;
      const gestures = await storage.getGesturesByRegion(region);
      res.json(gestures);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch gestures" });
    }
  });

  // Get recent gestures
  app.get("/api/gestures/recent", async (req, res) => {
    try {
      const limit = req.query.limit ? parseInt(req.query.limit as string) : 10;
      const gestures = await storage.getRecentGestures(limit);
      res.json(gestures);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch recent gestures" });
    }
  });

  // AI gesture analysis endpoint
  app.post("/api/analyze-gesture", async (req, res) => {
    try {
      const { imageData } = req.body;

      if (!imageData) {
        return res.status(400).json({ error: "No image data provided" });
      }

      // Remove data:image/jpeg;base64, prefix if present
      const base64Image = imageData.replace(/^data:image\/[a-z]+;base64,/, '');

      const result = await analyzeGestureImage(base64Image);

      res.json(result);
    } catch (error) {
      console.error('Gesture analysis error:', error);
      res.status(500).json({ error: "Failed to analyze gesture" });
    }
  });

  // Health check endpoint
  app.get("/api/health", (req, res) => {
    res.json({ status: "ok", timestamp: new Date().toISOString() });
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { type User, type InsertUser, type Gesture, type InsertGesture } from "@shared/schema";
import { randomUUID } from "crypto";

export interface IStorage {
  getUser(id: string): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;
  createGesture(gesture: InsertGesture): Promise<Gesture>;
  getGesturesByRegion(region: string): Promise<Gesture[]>;
  getRecentGestures(limit?: number): Promise<Gesture[]>;
}

export class MemStorage implements IStorage {
  private users: Map<string, User>;
  private gestures: Map<string, Gesture>;

  constructor() {
    this.users = new Map();
    this.gestures = new Map();
  }

  async getUser(id: string): Promise<User | undefined> {
    return this.users.get(id);
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(
      (user) => user.username === username,
    );
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const id = randomUUID();
    const user: User = { ...insertUser, id };
    this.users.set(id, user);
    return user;
  }

  async createGesture(insertGesture: InsertGesture): Promise<Gesture> {
    const id = randomUUID();
    const gesture: Gesture = { 
      ...insertGesture,
      userId: insertGesture.userId || null,
      isEmergency: insertGesture.isEmergency || false,
      id,
      timestamp: new Date()
    };
    this.gestures.set(id, gesture);
    return gesture;
  }

  async getGesturesByRegion(region: string): Promise<Gesture[]> {
    return Array.from(this.gestures.values())
      .filter(gesture => gesture.region === region)
      .sort((a, b) => (b.timestamp?.getTime() || 0) - (a.timestamp?.getTime() || 0));
  }

  async getRecentGestures(limit: number = 10): Promise<Gesture[]> {
    return Array.from(this.gestures.values())
      .sort((a, b) => (b.timestamp?.getTime() || 0) - (a.timestamp?.getTime() || 0))
      .slice(0, limit);
  }
}

export const storage = new MemStorage();

server/vite.ts.content:
import express, { type Express } from "express";
import fs from "fs";
import path from "path";
import { createServer as createViteServer, createLogger } from "vite";
import { type Server } from "http";
import viteConfig from "../vite.config";
import { nanoid } from "nanoid";

const viteLogger = createLogger();

export function log(message: string, source = "express") {
  const formattedTime = new Date().toLocaleTimeString("en-US", {
    hour: "numeric",
    minute: "2-digit",
    second: "2-digit",
    hour12: true,
  });

  console.log(`${formattedTime} [${source}] ${message}`);
}

export async function setupVite(app: Express, server: Server) {
  const serverOptions = {
    middlewareMode: true,
    hmr: { server },
    allowedHosts: true as const,
  };

  const vite = await createViteServer({
    ...viteConfig,
    configFile: false,
    customLogger: {
      ...viteLogger,
      error: (msg, options) => {
        viteLogger.error(msg, options);
        process.exit(1);
      },
    },
    server: serverOptions,
    appType: "custom",
  });

  app.use(vite.middlewares);
  app.use("*", async (req, res, next) => {
    const url = req.originalUrl;

    try {
      const clientTemplate = path.resolve(
        import.meta.dirname,
        "..",
        "client",
        "index.html",
      );

      // always reload the index.html file from disk incase it changes
      let template = await fs.promises.readFile(clientTemplate, "utf-8");
      template = template.replace(
        `src="/src/main.tsx"`,
        `src="/src/main.tsx?v=${nanoid()}"`,
      );
      const page = await vite.transformIndexHtml(url, template);
      res.status(200).set({ "Content-Type": "text/html" }).end(page);
    } catch (e) {
      vite.ssrFixStacktrace(e as Error);
      next(e);
    }
  });
}

export function serveStatic(app: Express) {
  const distPath = path.resolve(import.meta.dirname, "public");

  if (!fs.existsSync(distPath)) {
    throw new Error(
      `Could not find the build directory: ${distPath}, make sure to build the client first`,
    );
  }

  app.use(express.static(distPath));

  // fall through to index.html if the file doesn't exist
  app.use("*", (_req, res) => {
    res.sendFile(path.resolve(distPath, "index.html"));
  });
}

shared/schema.ts.content:
import { sql } from "drizzle-orm";
import { pgTable, text, varchar, timestamp, real, boolean, integer } from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

export const users = pgTable("users", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  username: text("username").notNull().unique(),
  password: text("password").notNull(),
});

export const gestures = pgTable("gestures", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: varchar("user_id"),
  region: varchar("region", { length: 10 }).notNull(),
  gestureText: text("gesture_text").notNull(),
  translatedText: text("translated_text").notNull(),
  confidence: real("confidence").notNull(),
  isEmergency: boolean("is_emergency").default(false),
  latency: integer("latency").notNull(), // in milliseconds
  timestamp: timestamp("timestamp").defaultNow(),
});

export const insertUserSchema = createInsertSchema(users).pick({
  username: true,
  password: true,
});

export const insertGestureSchema = createInsertSchema(gestures).omit({
  id: true,
  timestamp: true,
});

export type InsertUser = z.infer<typeof insertUserSchema>;
export type User = typeof users.$inferSelect;
export type InsertGesture = z.infer<typeof insertGestureSchema>;
export type Gesture = typeof gestures.$inferSelect;

client/src/App.tsx.content:
import { Switch, Route } from "wouter";
import { queryClient } from "./lib/queryClient";
import { QueryClientProvider } from "@tanstack/react-query";
import { Toaster } from "@/components/ui/toaster";
import { TooltipProvider } from "@/components/ui/tooltip";
import Home from "@/pages/home";
import NotFound from "@/pages/not-found";

function Router() {
  return (
    <Switch>
      <Route path="/" component={Home} />
      <Route component={NotFound} />
    </Switch>
  );
}

function App() {
  return (
    <QueryClientProvider client={queryClient}>
      <TooltipProvider>
        <Toaster />
        <Router />
      </TooltipProvider>
    </QueryClientProvider>
  );
}

export default App;

