  /* Better button visibility */
  .bg-primary {
    background: linear-gradient(135deg, var(--neon-teal), var(--neon-magenta)) !important;
    color: white !important;
    font-weight: 600;
    text-shadow: 0 1px 2px rgba(0, 0, 0, 0.5);
  }

  .bg-accent {
    background: linear-gradient(135deg, var(--neon-magenta), var(--neon-amber)) !important;
    color: white !important;
    font-weight: 600;
    text-shadow: 0 1px 2px rgba(0, 0, 0, 0.5);
  }

  /* Header styling for better contrast */
  header h1 {
    font-weight: 700 !important;
    font-size: 1.5rem !important;
  }

  header p {
    color: hsl(215, 8%, 70%) !important;
    font-weight: 500;
  }
}

@layer components {
  .custom-cursor {
    position: fixed;
    width: 20px;
    height: 20px;
    border: 2px solid var(--neon-teal);
    border-radius: 50%;
    pointer-events: none;
    z-index: 9999;
    transition: all 0.15s ease;
    box-shadow: 0 0 15px hsla(var(--neon-teal), 0.5);
  }

  .custom-cursor.pointer {
    width: 12px;
    height: 12px;
    background: var(--neon-teal);
    border: none;
    box-shadow: 0 0 20px hsla(var(--neon-teal), 0.8);
  }

  .custom-cursor.listening {
    width: 30px;
    height: 30px;
    background: radial-gradient(circle, hsla(var(--neon-teal), 0.3), transparent 70%);
    border: 2px solid var(--neon-teal);
    animation: pulse-listening 1.5s infinite;
  }

  .gradient-border {
    position: relative;
    background: linear-gradient(135deg, var(--neon-teal), var(--neon-magenta), var(--neon-amber));
    padding: 2px;
    border-radius: 12px;
  }

  .gradient-border > div {
    background: var(--card);
    border-radius: 10px;
    height: 100%;
  }

  .scanning-overlay {
    position: absolute;
    inset: 0;
    background: linear-gradient(45deg, transparent 30%, hsla(var(--neon-teal), 0.1) 50%, transparent 70%);
    animation: scan-sweep 2s infinite;
    border-radius: 12px;
  }

  .glow-effect {
    box-shadow: 0 0 20px hsla(var(--neon-teal), 0.3), 
                0 0 40px hsla(var(--neon-teal), 0.1),
                inset 0 1px 0 hsla(var(--neon-teal), 0.2);
  }

  .confidence-bar {
    height: 4px;
    background: linear-gradient(90deg, 
        var(--destructive) 0%, 
        var(--neon-amber) 50%, 
        var(--neon-teal) 100%);
    border-radius: 2px;
  }

  .gesture-point {
    position: absolute;
    width: 8px;
    height: 8px;
    background: var(--neon-teal);
    border-radius: 50%;
    box-shadow: 0 0 10px hsla(var(--neon-teal), 0.8);
    animation: pulse-point 1s infinite;
  }
}

@keyframes pulse-listening {
  0%, 100% { 
    transform: scale(1); 
    box-shadow: 0 0 15px hsla(var(--neon-teal), 0.5); 
  }
  50% { 
    transform: scale(1.2); 
    box-shadow: 0 0 25px hsla(var(--neon-teal), 0.8); 
  }
}

@keyframes scan-sweep {
  0% { transform: translateX(-100%) rotate(45deg); }
  100% { transform: translateX(200%) rotate(45deg); }
}

@keyframes pulse-point {
  0%, 100% { transform: scale(1); opacity: 1; }
  50% { transform: scale(1.5); opacity: 0.7; }
}

@media (prefers-reduced-motion: reduce) {
  * {
    animation-duration: 0.01ms !important;
    animation-iteration-count: 1 !important;
  }
}

